54 55 void insert50RandomForProfiling() { 56 RandomMapper m = getMapper(); 57 List&lt;RandomModel&gt; genRnd = new LinkedList&lt;&gt;(); 58 RNDRandomModelFactory factory = new RNDRandomModelFactory(getContext()); 59 for(int i = 0; i&lt;50; i++) { 60 genRnd.add(factory.newRandomModel()); 61 } 62 m.insertInTransaction(genRnd); 63 } 64 65 // Fab menu section 66 67 @Override 68 public View.OnClickListener helpFabMenuAction() { 69 return new View.OnClickListener() { 70 @Override 71 public void onClick(View v) { 72 ((KittyTutorialActivity)getActivity()).showWebViewDialog(LessonsUriConstants.L2_T5_TUTORIAL); 73 } 74 }; 75 } 76 77 @Override 78 public View.OnClickListener sourceFabMenuAction() { 79 return new View.OnClickListener() { 80 @Override 81 public void onClick(View v) { 82 ((KittyTutorialActivity)getActivity()).showWebViewDialog(LessonsUriConstants.L2_T5_SOURCE); 83 } 84 }; 85 } 86 87 @Override 88 public View.OnClickListener schemaFabMenuAction() { 89 return new View.OnClickListener() { 90 @Override 91 public void onClick(View v) { 92 ((KittyTutorialActivity)getActivity()).showWebViewDialog(LessonsUriConstants.L2_T5_SCHEMA); 93 } 94 }; 95 } 96 97 @Override 98 protected int snackbarMessageResource() { 99 return R.string._l2_t5_snackbar_message; 100 } 101 102 final static int OPERATION_TEN_NEW = 0; 103 final static int OPERATION_FIFTY_K_NEW = 1; 104 final static int OPERATION_FIFTY_K_TX_NEW = 2; 105 106 class InsertInAsync extends AsyncTask&lt;String, String, Integer&gt; { 107 108 // It is not profiling (!) 109 private long nanosStart; 110 private long nanosGetDatabaseAnaMapper; 111 private long nanosGenerateRandomModelsToInsert; 112 private long nanosInsert; 113 114 private final int operation; 115 116 private final boolean useNewDatabaseObject; 117 118 ProgressDialog dialog; 119 120 InsertInAsync(int operation, boolean useNewDatabaseObject) { 121 this.operation = operation; 122 this.useNewDatabaseObject = useNewDatabaseObject; 123 } 124 125 BasicDatabase getDatabase() { 126 if(useNewDatabaseObject) 127 return new BasicDatabase(Lesson2Tab5Random.this.getContext()); 128 else 129 return Lesson2Tab5Random.this.getDb(); 130 } 131 132 RandomMapper getRandomMapperForAssync() { 133 return (RandomMapper) getDatabase().getMapper(RandomModel.class); 134 } 135 136 void insertIntoDB() { 137 RNDRandomModelFactory rndFactory = new RNDRandomModelFactory(Lesson2Tab5Random.this.getContext()); 138 nanosStart = System.nanoTime(); 139 RandomMapper mapper = getRandomMapperForAssync(); 140 nanosGetDatabaseAnaMapper = System.nanoTime(); 141 List&lt;RandomModel&gt; toInsert = new ArrayList&lt;&gt;(); 142 switch (operation) { 143 case OPERATION_TEN_NEW: 144 for(int i = 0; i &lt; 10; i++) { 145 toInsert.add(rndFactory.newRandomModel()); 146 } 147 break; 148 default: 149 for(int i = 0; i &lt; 50000; i++) { 150 toInsert.add(rndFactory.newRandomModel()); 151 } 152 } 153 nanosGenerateRandomModelsToInsert = System.nanoTime(); 154 switch (operation) { 155 case OPERATION_TEN_NEW: 156 mapper.insert(toInsert); 157 break; 158 case OPERATION_FIFTY_K_TX_NEW: 159 mapper.insertInTransaction(toInsert); 160 break; 161 case OPERATION_FIFTY_K_NEW: 162 mapper.insert(toInsert); 163 break; 164 } 165 nanosInsert = System.nanoTime(); 166 mapper.close(); 167 } 168 169 /** 170* Override this method to perform a computation on a background thread. The 171* specified parameters are the parameters passed to {@link #execute} 172* by the caller of this task. 173* &lt;p&gt; 174* This method can call {@link #publishProgress} to publish updates 175* on the UI thread. 176* 177* @param strings The parameters of the task. 178* @return A result, defined by the subclass of this task. 179* @see #onPreExecute() 180* @see #onPostExecute 181* @see #publishProgress 182*/ 183 @Override 184 protected Integer doInBackground(String... strings) { 185 try { 186 insertIntoDB(); 187 } catch (Exception e) { 188 Log.e(BasicDatabase.LOG_TAG, &#34;Exception caught on insertion, see details&#34;, e); 189 if(e instanceof KittyRuntimeException) 190 if(((KittyRuntimeException) e).getNestedException() != null) 191 Log.e(BasicDatabase.LOG_TAG, &#34;Nested exception: &#34;, ((KittyRuntimeException) e).getNestedException()); 192 return -1; 193 } 194 return 1; 195 } 196 197 @Override 198 protected void onPreExecute() { 199 int progressDialogStringId = 0; 200 switch (operation) { 201 case OPERATION_TEN_NEW: 202 progressDialogStringId = R.string._l2_t5_inserting_10; 203 break; 204 case OPERATION_FIFTY_K_NEW: 205 progressDialogStringId = R.string._l2_t5_inserting_50k; 206 break; 207 case OPERATION_FIFTY_K_TX_NEW: 208 progressDialogStringId = R.string._l2_t5_inserting_50kTX; 209 break; 210 } 211 dialog = ProgressDialog.show( 212 Lesson2Tab5Random.this.getLessonActivity(), 213 Lesson2Tab5Random.this.getString(R.string._l2_t5_inserting_dialog_title), 214 Lesson2Tab5Random.this.getString(progressDialogStringId) 215 ); 216 dialog.setCancelable(false); 217 } 218 219 @Override 220 protected void onPostExecute(Integer result) { 221 int operationNameStringId = 0; 222 switch (operation) { 223 case OPERATION_TEN_NEW: 224 operationNameStringId = R.string._l2_t5_inserting_10_OP; 225 break; 226 case OPERATION_FIFTY_K_NEW: 227 operationNameStringId = R.string._l2_t5_inserting_50k_OP; 228 break; 229 case OPERATION_FIFTY_K_TX_NEW: 230 operationNameStringId = R.string._l2_t5_inserting_50kTX_OP; 231 break; 232 } 233 if(result &gt; 0) { 234 235 String successfullEvent = format( 236 Lesson2Tab5Random.this.getString(R.string._l2_t5_inserted_event), 237 Lesson2Tab5Random.this.getString(operationNameStringId), 238 useNewDatabaseObject, 239 nanosInsert - nanosStart, 240 nanosGetDatabaseAnaMapper - nanosStart, 241 nanosGenerateRandomModelsToInsert - nanosGetDatabaseAnaMapper, 242 nanosInsert - nanosGenerateRandomModelsToInsert 243 ); 244 Lesson2Tab5Random.this.addNewEventToExpandedPanel(successfullEvent); 245 } else { 246 String eventErrorMessage = format( 247 Lesson2Tab5Random.this.getString(R.string._l2_t5_inserted_event_error), 248 Lesson2Tab5Random.this.getString(operation), 249 BasicDatabase.LOG_TAG 250 ); 251 Lesson2Tab5Random.this.addNewEventToExpandedPanel(eventErrorMessage); 252 } 253 dialog.cancel(); 254 countRecordsToExpandedPanel(getMapper()); 255 } 256 } 257}

 KittyORM delete example  1// Initializing database instance 2BasicDatabase db = new BasicDatabase(getContext()); 3// Getting mapper instance 4RandomMapper mapper = (RandomMapper) db.getMapper(RandomModel.class); 5// Getting existing model from database (assuming that 0l model exists) 6RandomModel toDelete = mapper.findByIPK(0l); 7// Deleting model 8long rowsAffected = mapper.delete(toDelete); 1// Deleting by random_int range 2BasicDatabase db = new BasicDatabase(getContext()); 3// Getting mapper instance 4RandomMapper mapper = (RandomMapper) db.getMapper(RandomModel.class); 5// Creating clause for deletion 6SQLiteCondition condition = new SQLiteConditionBuilder() 7 .addField(&#34;random_int&#34;) 8 .addSQLOperator(GREATER_OR_EQUAL) 9 .addValue(0) 10 .addSQLOperator(AND) 11 .addField(&#34;random_int&#34;) 12// Just as tip, you can pass SQLite operator as string, not only as 13// SQLiteOperator enum element 14 .addSQLOperator(&#34;&lt;=&#34;) 15 .addValue(10000) 16 .build(); 17// Deleting with generated clause 18mapper.deleteWhere(condition); 19 20// Also, you may use pass condition as SQLite string 21mapper.deleteWhere(&#34;random_int &gt;= ? AND random_int &lt;= ?&#34;, 0, 10000) 22 23// And, finally, in those string you may use POJO field names in #?fieldName 24mapper.deleteWhere(&#34;#?randomInt &gt;= ? AND #?randomInt &lt;= ?&#34;, 0, 10000)  KittyORM SQLiteConditionBuilder example 1SQLiteCondition condition = new SQLiteConditionBuilder() 2 .addColumn(&#34;a_column&#34;) 3 .addSQLOperator(EQUAL) 4 .addValue(&#34;a&#34;) 5 .addSQLOperator(AND) 6 .addSQLOperator(OPEN_SUBC) 7 .addColumn(&#34;b_column&#34;) 8 .addSQLOperator(GREATER_THAN) 9 .addValue(0) 10 .addSQLOperator(OR) 11 .addSQLOperator(BETWEEN) 12 .addValue(10) 13 .addSQLOperator(AND) 14 .addValue(20) 15 .addSQLOperator(CLOSE_SUBC) 16 .build(); 17 18// Note that following condition is equal to previous one 19SQLiteCondition condition2 = new SQLiteConditionBuilder() 20 .addColumn(&#34;a_column&#34;) 21 .addSQLOperator(&#34;=&#34;) 22 .addValue(&#34;a&#34;) 23 .addSQLOperator(&#34;AND&#34;) 24 .addSQLOperator(&#34;(&#34;) 25 .addColumn(&#34;b_column&#34;) 26 .addSQLOperator(&#34;&gt;=&#34;) 27 .addValue(0) 28 .addSQLOperator(&#34;OR&#34;) 29 .addSQLOperator(&#34;BETWEEN&#34;) 30 .addValue(10) 31 .addSQLOperator(&#34;AND&#34;) 32 .addValue(20) 33 .addSQLOperator(&#34;)&#34;) 34 .build(); 35 36// And, finally, you can pass to condition builder not only column names but POJO field names as well 37SQLiteCondition condition3 = new SQLiteConditionBuilder() 38 .addField(&#34;aColumn&#34;, SomeModel.class) 39 .addSQLOperator(&#34;=&#34;) 40 .addValue(&#34;a&#34;) 41 .addSQLOperator(&#34;AND&#34;) 42 .addSQLOperator(&#34;(&#34;) 43 .addField(&#34;bColumn&#34;, SomeModel.class) 44 .addSQLOperator(&#34;&gt;=&#34;) 45 .addValue(0) 46 .addSQLOperator(&#34;OR&#34;) 47 .addSQLOperator(&#34;BETWEEN&#34;) 48 .addValue(10) 49 .addSQLOperator(&#34;AND&#34;) 50 .addValue(20) 51 .addSQLOperator(&#34;)&#34;) 52 .build(); 53 54// Also, if you would like use SQLite string to build condition, you may use following method: 55SQLiteCondition condition4 = SQLiteConditionBuilder.fromSQL(&#34;WHERE a_column = ? AND (b_column &gt; ? OR BETWEEN ? AND ?)&#34;, SomeModel.class, &#34;a&#34;, 0, 10, 20); 56 57// And, for SQLite string, you may pass POJO field names as well using #?fieldName syntax 58SQLiteCondition condition5 = SQLiteConditionBuilder.fromSQL(&#34;WHERE #?aColumn = ? AND (#?bColumn &gt; ? OR BETWEEN ? AND ?)&#34;, SomeModel.class, &#34;a&#34;, 0, 10, 20); KittyORM basic_database implementation sources     Click to view BasicDatabase.class:  1@KITTY_DATABASE( 2 databaseName = &#34;basic_database&#34;, 3 domainPackageNames = {&#34;net.akaish.kittyormdemo.sqlite.basicdb&#34;}, 4 logTag = LOG_TAG, 5 isLoggingOn = true, 6 isProductionOn = true, 7 isPragmaOn = true 8) 9@KITTY_DATABASE_REGISTRY( 10 domainPairs = { 11 @KITTY_REGISTRY_PAIR(model = ComplexRandomModel.class, mapper = ComplexRandomMapper.class), 12 @KITTY_REGISTRY_PAIR(model = IndexesAndConstraintsModel.class), 13 @KITTY_REGISTRY_PAIR(model = RandomModel.class, mapper = RandomMapper.class) 14 } 15) 16public class BasicDatabase extends KittyDatabase { 17 18 public static final String LOG_TAG = &#34;BASIC DB DEMO&#34;; 19 20 /** 21* KittyORM main database class that represents bootstrap and holder for all related with database 22* components. 23* &lt;br&gt; See {@link KittyDatabase#KittyDatabase(Context, String)} for more info. 24* 25* @param ctx 26*/ 27 public BasicDatabase(Context ctx) { 28 super(ctx); 29 } 30 31} 
