    Click to view RandomMapper.class:  1package net.akaish.kittyormdemo.sqlite.basicdb; 2 3import net.akaish.kitty.orm.KittyMapper; 4import net.akaish.kitty.orm.KittyModel; 5import net.akaish.kitty.orm.configuration.conf.KittyTableConfiguration; 6import net.akaish.kitty.orm.query.QueryParameters; 7import net.akaish.kitty.orm.query.conditions.SQLiteCondition; 8import net.akaish.kitty.orm.query.conditions.SQLiteConditionBuilder; 9import net.akaish.kitty.orm.query.conditions.SQLiteOperator; 10import net.akaish.kitty.orm.util.KittyConstants; 11import net.akaish.kittyormdemo.sqlite.misc.Animals; 12 13import static net.akaish.kitty.orm.query.conditions.SQLiteOperator.AND; 14import static net.akaish.kitty.orm.query.conditions.SQLiteOperator.LESS_OR_EQUAL; 15import static net.akaish.kitty.orm.query.conditions.SQLiteOperator.LESS_THAN; 16import static net.akaish.kitty.orm.query.conditions.SQLiteOperator.GREATER_OR_EQUAL; 17import static net.akaish.kitty.orm.query.conditions.SQLiteOperator.GREATER_THAN; 18import static net.akaish.kittyormdemo.sqlite.basicdb.AbstractRandomModel.RND_ANIMAL_CNAME; 19 20import java.util.List; 21 22 23/** 24* Created by akaish on 09.08.18. 25* @author akaish (Denis Bogomolov) 26*/ 27public class RandomMapper extends KittyMapper { 28 29 public &lt;M extends KittyModel&gt; RandomMapper(KittyTableConfiguration tableConfiguration, 30 M blankModelInstance, 31 String databasePassword) { 32 super(tableConfiguration, blankModelInstance, databasePassword); 33 } 34 35 protected SQLiteCondition getAnimalCondition(Animals animal) { 36 return new SQLiteConditionBuilder() 37 .addColumn(RND_ANIMAL_CNAME) 38 .addSQLOperator(&#34;=&#34;) 39 .addObjectValue(animal) 40 .build(); 41 } 42 43 public long deleteByRandomIntegerRange(int start, int end) { 44 return deleteWhere(&#34;#?randomInt &gt;= ? AND #?randomInt &lt;= ?&#34;, start, end); 45 } 46 47 public long deleteByAnimal(Animals animal) { 48 return deleteWhere(getAnimalCondition(animal)); 49 } 50 51 public List&