    Click to view RandomMapper.class:  1package net.akaish.kittyormdemo.sqlite.basicdb; 2 3import net.akaish.kitty.orm.KittyMapper; 4import net.akaish.kitty.orm.KittyModel; 5import net.akaish.kitty.orm.configuration.conf.KittyTableConfiguration; 6import net.akaish.kitty.orm.query.QueryParameters; 7import net.akaish.kitty.orm.query.conditions.SQLiteCondition; 8import net.akaish.kitty.orm.query.conditions.SQLiteConditionBuilder; 9import net.akaish.kitty.orm.query.conditions.SQLiteOperator; 10import net.akaish.kitty.orm.util.KittyConstants; 11import net.akaish.kittyormdemo.sqlite.misc.Animals; 12 13import static net.akaish.kitty.orm.query.conditions.SQLiteOperator.AND; 14import static net.akaish.kitty.orm.query.conditions.SQLiteOperator.LESS_OR_EQUAL; 15import static net.akaish.kitty.orm.query.conditions.SQLiteOperator.LESS_THAN; 16import static net.akaish.kitty.orm.query.conditions.SQLiteOperator.GREATER_OR_EQUAL; 17import static net.akaish.kitty.orm.query.conditions.SQLiteOperator.GREATER_THAN; 18import static net.akaish.kittyormdemo.sqlite.basicdb.AbstractRandomModel.RND_ANIMAL_CNAME; 19 20import java.util.List; 21 22 23/** 24* Created by akaish on 09.08.18. 25* @author akaish (Denis Bogomolov) 26*/ 27public class RandomMapper extends KittyMapper { 28 29 public &lt;M extends KittyModel&gt; RandomMapper(KittyTableConfiguration tableConfiguration, 30 M blankModelInstance, 31 String databasePassword) { 32 super(tableConfiguration, blankModelInstance, databasePassword); 33 } 34 35 protected SQLiteCondition getAnimalCondition(Animals animal) { 36 return new SQLiteConditionBuilder() 37 .addColumn(RND_ANIMAL_CNAME) 38 .addSQLOperator(&#34;=&#34;) 39 .addObjectValue(animal) 40 .build(); 41 } 42 43 public long deleteByRandomIntegerRange(int start, int end) { 44 return deleteWhere(&#34;#?randomInt &gt;= ? AND #?randomInt &lt;= ?&#34;, start, end); 45 } 46 47 public long deleteByAnimal(Animals animal) { 48 return deleteWhere(getAnimalCondition(animal)); 49 } 50 51 public List&lt;RandomModel&gt; findByAnimal(Animals animal, long offset, long limit, boolean groupingOn) { 52 SQLiteCondition condition = getAnimalCondition(animal); 53 QueryParameters qparam = new QueryParameters(); 54 qparam.setLimit(limit).setOffset(offset); 55 if(groupingOn) 56 qparam.setGroupByColumns(RND_ANIMAL_CNAME); 57 else 58 qparam.setGroupByColumns(KittyConstants.ROWID); 59 return findWhere(condition, qparam); 60 } 61 62 public List&lt;RandomModel&gt; findByIdRange(long fromId, long toId, boolean inclusive, Long offset, Long limit) { 63 SQLiteCondition condition = new SQLiteConditionBuilder() 64 .addColumn(&#34;id&#34;) 65 .addSQLOperator(inclusive ? GREATER_OR_EQUAL : GREATER_THAN) 66 .addValue(fromId) 67 .addSQLOperator(AND) 68 .addColumn(&#34;id&#34;) 69 .addSQLOperator(inclusive ? LESS_OR_EQUAL : LESS_THAN) 70 .addValue(toId) 71 .build(); 72 QueryParameters qparam = new QueryParameters(); 73 qparam.setLimit(limit).setOffset(offset).setGroupByColumns(KittyConstants.ROWID); 74 return findWhere(condition, qparam); 75 } 76 77 public List&lt;RandomModel&gt; findAllRandomModels(Long offset, Long limit) { 78 QueryParameters qparam = new QueryParameters(); 79 qparam.setLimit(limit).setOffset(offset).setGroupByColumns(KittyConstants.ROWID); 80 return findAll(qparam); 81 } 82 83} 
