mals animal = Animals.valueOf(animalEnumStringValue); 115 toUpdate.randomAnimal = animal; 116 toUpdate.randomAnimalName = getString(Animals.getLocalizedAnimalNameResource(animal)); 117 toUpdate.randomAnimalSays = getString(Animals.getLocalizedAnimalSaysResource(animal)); 118 long updateStatus = mapper.update(toUpdate); 119 if(updateStatus &gt; 0) { 120 addNewEventToExpandedPanel(format(getString(R.string._l2_t2_expanded_added), toUpdate.getRowID(), originalModel, toUpdate, mapper.countAll())); 121 } else { 122 addNewEventToExpandedPanel(format(getString(R.string._l2_t2_expanded_error), toUpdate)); 123 } 124 mapper.close(); 125 if(isLoadedModelExists()) { 126 modelExists(); 127 } else { 128 noModel(); 129 } 130 } else { 131 addNewEventToExpandedPanel(format(getString(R.string._l2_t2_expanded_error_unable), getLoadedModelId())); 132 } 133 } 134 135 void loadModel() { 136 String modelIdFromLoadET = loadModelIdET.getText().toString(); 137 if(modelIdFromLoadET == null) { 138 getLessonActivity().showWarningDialog( 139 R.string._warning_dialog_title, 140 R.string._l2_t2_warning_bad_id_input, 141 R.string._warning_dialog_ok_button_text); 142 return; 143 } 144 if(modelIdFromLoadET.length() == 0) { 145 getLessonActivity().showWarningDialog( 146 R.string._warning_dialog_title, 147 R.string._l2_t2_warning_bad_id_input, 148 R.string._warning_dialog_ok_button_text); 149 return; 150 } 151 Long modelToLoad = null; 152 try { 153 modelToLoad = Long.valueOf(modelIdFromLoadET); 154 } catch (Exception e) { 155 getLessonActivity().showWarningDialog( 156 R.string._warning_dialog_title, 157 R.string._l2_t2_warning_bad_id_input, 158 R.string._warning_dialog_ok_button_text); 159 return; 160 } 161 setLoadedModelId(modelToLoad); 162 if(isLoadedModelExists()) 163 modelExists(); 164 else { 165 getLessonActivity().showWarningDialog( 166 R.string._warning_dialog_title, 167 R.string._l2_t2_warning_no_record_with_id, 168 R.string._warning_dialog_ok_button_text); 169 noModel(); 170 } 171 } 172 173 @Override 174 public void onVisible() { 175 new LoadSelectedModel().execute(0l); 176 } 177 178 void modelExists() { 179 setUpdateFieldsActive(true); 180 loadModelData(getLoadedModelId()); 181 } 182 183 void noModel() { 184 setUpdateFieldsActive(false); 185 setLoadedModelTWText(null); 186 } 187 188 void loadModelData(Long IPK) { 189 RandomMapper mapper = getMapper(); 190 RandomModel model = mapper.findByIPK(IPK); 191 if(model == null) { 192 setUpdateFieldsActive(false); 193 } else { 194 loadModelValues(model); 195 } 196 setLoadedModelTWText(model); 197 } 198 199 void setLoadedModelTWText(RandomModel model) { 200 if(model == null) { 201 loadedModelTW.setText( 202 format( 203 getString(R.string._l2_t2_current_model_pattern), 204 getString(R.string._l2_t2_current_model_not_set) 205 ) 206 ); 207 } else { 208 loadedModelTW.setText( 209 format( 210 getString(R.string._l2_t2_current_model_pattern), 211 model 212 ) 213 ); 214 } 215 } 216 217 void loadModelValues(RandomModel model) { 218 loadModelIdET.setText(Long.toString(model.id)); 219 randomIntET.setText(Integer.toString(model.randomInt)); 220 randomIntegerET.setText(Integer.toString(model.randomInteger)); 221 randomAnimalSaysET.setText(model.randomAnimalSays); 222 randomAnimalNameET.setText(model.randomAnimalName); 223 animalSpinner.setSelection(animalAdapter.getPosition(model.randomAnimal.name()), true); 224 } 225 226 void setUpdateFieldsActive(boolean isActive) { 227 //randomIntET.setFocusable(isActive); 228 randomIntET.setEnabled(isActive); 229 230 //randomIntegerET.setFocusable(isActive); 231 randomIntegerET.setEnabled(isActive); 232 233 //updateButton.setFocusable(isActive); 234 updateButton.setEnabled(isActive); 235 236 //animalSpinner.setFocusable(isActive); 237 animalSpinner.setEnabled(isActive); 238 if(!isActive) 239 animalSpinner.setVisibility(View.GONE); 240 else 241 animalSpinner.setVisibility(View.VISIBLE); 242 243 randomAnimalSaysET.setEnabled(isActive); 244 randomAnimalNameET.setEnabled(isActive); 245 } 246 