&ldquo;RUN CRUD OPERATIONS&rdquo; code    Click to view GettingStartedTutorialCode.allInOne() 1// Creating new instance of SimpleDatabase 2SimpleDatabase simpleDatabase = new SimpleDatabase(context); 3 4KittyMapper mapper = simpleDatabase.getMapper(SimpleExampleModel.class); 5 6// Counting records in db table and deleting them if table not empty 7if(mapper.countAll() &gt; 0) 8 mapper.deleteAll(); 9// Insert new model example 10// Creating and setting three new models 11SimpleExampleModel alex = new SimpleExampleModel(); 12 13alex.randomInteger = 545141; 14alex.firstName = &#34;Alex&#34;; 15 16SimpleExampleModel marina = new SimpleExampleModel(); 17 18marina.randomInteger = 228; 19marina.firstName = &#34;Marina&#34;; 20 21SimpleExampleModel marina2 = new SimpleExampleModel(); 22 23marina2.randomInteger = 445555; 24marina2.firstName = &#34;Marina&#34;; 25 26// Saving those models 27// Saving model with mapper.save(M model) 28mapper.save(alex); 29mapper.save(marina2); 30 31// Saving model with mapper.insert(M model) 32// Better to use insert(M model) for new DB records cause it is little bit faster 33long marinaRowid = mapper.insert(marina); 34 35 36// Finding existing records in DB and mapping them to entities 37 38int findOperationId = 0; 39List&lt;SimpleExampleModel&gt; marinas; 40 41// find with condition 42SQLiteConditionBuilder builder = new SQLiteConditionBuilder(); 43builder.addColumn(&#34;first_name&#34;) 44 .addSQLOperator(SQLiteOperator.EQUAL) 45 .addValue(&#34;Marina&#34;); 46marinas = mapper.findWhere(builder.build()); 47 48// find with condition (you may use shorter syntax) 49builder = new SQLiteConditionBuilder(); 50builder.addColumn(&#34;first_name&#34;) 51 .addSQLOperator(&#34;=&#34;) // You may use string operators instead SQLiteOperator enum element 52 .addValue(&#34;Marina&#34;); 53marinas = mapper.findWhere(builder.build()); 54 55// find with condition (without query builder) 56marinas = mapper.findWhere(&#34;first_name = ?&#34;, &#34;Marina&#34;); 57 58// find with condition (pass POJO field name as parameter, in ?#fieldName; form) 59marinas = mapper.findWhere(&#34;?#firstName; = ?&#34;, &#34;Marina&#34;); 60 61 62findOperationId++; 63// find with RowID 64SimpleExampleModel marinaFromTableRowid = mapper.findByRowID(marinaRowid); 65 66findOperationId++; 67// find with IPK 68SimpleExampleModel marinaFromTableIPK = mapper.findByIPK(marinaFromTableRowid.id); 69 70findOperationId++; 71// find with KittyPrimaryKey 72KittyPrimaryKey pk = new KittyPrimaryKeyBuilder() 73 .addKeyColumnValue(&#34;id&#34;, marinaFromTableRowid.id.toString()) 74 .build(); 75SimpleExampleModel marinaFromTableKPK = mapper.findByPK(pk); 76 77 78// Generating and inserting list of 10 random models 79List&lt;SimpleExampleModel&gt; randomModels = new LinkedList&lt;&gt;(); 80for(int i = 0; i &lt; 10; i++) 81 randomModels.add(RandomSimpleExampleModelUtil.randomSEModel()); 82mapper.save(randomModels); 83 84// Deleting some models 85// Deleting by entity, make sure that entity has RowID\IPK\PK set 86SQLiteCondition alexCondition = new SQLiteConditionBuilder() 87 .addColumn(&#34;first_name&#34;) 88 .addSQLOperator(SQLiteOperator.EQUAL) 89 .addValue(&#34;Alex&#34;) 90 .build(); 91SimpleExampleModel alexToDelete = mapper.findFirst(alexCondition); 92mapper.delete(alexToDelete); 93 94 95// Deleting with condition 96mapper.deleteWhere(&#34;random_integer = ?&#34;, marina2.randomInteger); 97 98// Updating models 99// updating current model 100// if model has RowId or IPK or PrimaryKey values set (3-rd is slowest) just 101SimpleExampleModel newMarina = marinaFromTableIPK.clone(SimpleExampleModel.class); 102newMarina.randomInteger = 1337; 103if(mapper.update(newMarina) &gt; 0) { 104 findOperationId++; 105 SimpleExampleModel marinaFromTableIPK2 = mapper.findByIPK(marinaFromTableRowid.id); 106} 107 108// another option is updating with generating query 109SimpleExampleModel updateMarina = new SimpleExampleModel(); 110updateMarina.randomInteger = 121212; 111builder = new SQLiteConditionBuilder(); 112builder.addColumn(&#34;first_name&#34;) 113 .addSQLOperator(SQLiteOperator.EQUAL) 114 .addValue(&#34;Marina&#34;); 115if(mapper.update(updateMarina, builder.build(), new String[]{&#34;randomInteger&#34;}, CVUtils.INCLUDE_ONLY_SELECTED_FIELDS) &gt; 0) { 116 findOperationId++; 117 // find with IPK 118 SimpleExampleModel marinaFromTableIPK2 = mapper.findByIPK(marinaFromTableRowid.id); 119} 120 121// bulk operations in TX mode 122LinkedList&lt;SimpleExampleModel&gt; randModels = new LinkedList&lt;&gt;(); 123for(int i = 0; i &lt; 10; i++) 124 randModels.add(RandomSimpleExampleModelUtil.randomSEModel()); 125mapper.saveInTransaction(randModels); 126 127// closing mapper 128mapper.close(); 
